from functools import lru_cache

class Solution:
    def maxScore(self, nums: List[int]) -> int:
        n = len(nums)

        @lru_cache(maxsize=None)
        def dfs(mask, step):
            if mask == (1 << n) - 1:
                return 0
            max_score = 0
            available_indices = [i for i in range(n) if not (mask & (1 << i))]
            m = len(available_indices)
            for i in range(m):
                for j in range(i + 1, m):
                    x = available_indices[i]
                    y = available_indices[j]
                    current_gcd = math.gcd(nums[x], nums[y])
                    current_score = step * current_gcd
                    new_mask = mask | (1 << x) | (1 << y)
                    remaining_score = dfs(new_mask, step + 1)
                    total_score = current_score + remaining_score
                    if total_score > max_score:
                        max_score = total_score
            return max_score

        return dfs(0, 1)
