class Solution:
    def minOperationsToMakeMedianK(self, nums: List[int], k: int) -> int:
        nums.sort()
        n = len(nums)
        median_pos = n // 2
        operations = 0

        if nums[median_pos] == k:
            return 0
        elif nums[median_pos] < k:
            operations += k - nums[median_pos]
            left = median_pos + 1
            right = n - 1
            while left <= right:
                mid = (left + right) // 2
                if nums[mid] < k:
                    left = mid + 1
                else:
                    right = mid - 1
            operations += k * (left - median_pos - 1) - sum(nums[median_pos + 1: left])
        else:
            operations += nums[median_pos] - k
            left = 0
            right = median_pos - 1
            while left <= right:
                mid = (left + right) // 2
                if nums[mid] > k:
                    right = mid - 1
                else:
                    left = mid + 1
            operations += sum(nums[right + 1: median_pos]) - k * (median_pos - right - 1)
        return operations
