class Solution:
    def minCapability(self, nums: List[int], k: int) -> int:
        left = min(nums)
        right = max(nums)

        def can_rob(mid):
            count = 0
            last_robbed = -2  # Initialize to a position that ensures the first house can be robbed
            for i in range(len(nums)):
                if nums[i] <= mid and i > last_robbed + 1:
                    count += 1
                    last_robbed = i
                    if count >= k:
                        return True
            return count >= k

