class Solution:
    def ways(self, pizza: List[str], k: int) -> int:
        rows = len(pizza)
        cols = len(pizza[0])
        mod = 10**9 + 7

        prefix = [[0] * (cols + 1) for _ in range(rows + 1)]
        for i in range(rows - 1, -1, -1):
            for j in range(cols - 1, -1, -1):
                prefix[i][j] = prefix[i + 1][j] + prefix[i][j + 1] - prefix[i + 1][j + 1] + (1 if pizza[i][j] == 'A' else 0)

        dp_prev = [[0] * cols for _ in range(rows)]
        for i in range(rows):
            for j in range(cols):
                if prefix[i][j] > 0:
                    dp_prev[i][j] = 1

        for m in range(2, k + 1):
            dp_curr = [[0] * cols for _ in range(rows)]
            col_sums = [[0] * (cols + 1) for _ in range(rows)]
            row_sums = [[0] * (cols) for _ in range(rows + 1)]

            for i in range(rows - 1, -1, -1):
                for j in range(cols - 1, -1, -1):
                    col_sums[i][j] = col_sums[i][j + 1] + (dp_prev[i][j] if i > 0 and prefix[i - 1][j] - prefix[i][j] > 0 else 0)

            for j in range(cols - 1, -1, -1):
                for i in range(rows - 1, -1, -1):
                    row_sums[i][j] = row_sums[i + 1][j] + (dp_prev[i][j] if j > 0 and prefix[i][j - 1] - prefix[i][j] > 0 else 0)

            for i in range(rows):
                for j in range(cols):
                    total = 0
                    if i + 1 < rows and prefix[i][j] - prefix[i + 1][j] > 0:
                        total += col_sums[i + 1][j]
                    if j + 1 < cols and prefix[i][j] - prefix[i][j + 1] > 0:
                        total += row_sums[i][j + 1]
                    dp_curr[i][j] = total % mod
            dp_prev = dp_curr

        return dp_prev[0][0] % mod
