class Solution:
    def minInteger(self, num: str, k: int) -> str:
        num = list(num)
        n = len(num)
        pos = [[] for _ in range(10)]
        for idx, ch in enumerate(num):
            pos[int(ch)].append(idx)

        res = []
        bit = [0] * (n + 2)

        def update(i, delta):
            while i <= n:
                bit[i] += delta
                i += i & -i

        def query(i):
            res = 0
            while i > 0:
                res += bit[i]
                i -= i & -i
            return res

        remaining = n
        for i in range(n):
            if k <= 0:
                break
            for d in range(10):
                if not pos[d]:
                    continue
                original_pos = pos[d][0]
                cost = original_pos - query(original_pos + 1)
                if cost <= k:
                    k -= cost
                    res.append(str(d))
                    update(original_pos + 1, 1)
                    pos[d].pop(0)
                    remaining -= 1
                    break

        remaining_chars = []
        for d in range(10):
            for p in pos[d]:
                remaining_chars.append((p, str(d)))
        remaining_chars.sort()
        res.extend([ch for _, ch in remaining_chars])

        return ''.join(res)
