                hat_to_people[hat].append(person)

        dp = [0] * (1 << n)
        dp[0] = 1

        for hat in range(1, 41):
            new_dp = dp.copy()
            people = hat_to_people[hat]
            for mask in range((1 << n) - 1, -1, -1):
                for person in people:
