class Solution:
    def deleteDuplicateFolder(self, paths: List[List[str]]) -> List[List[str]]:
        root = {}
        root["#"] = {}  # dummy root

        for path in paths:
            node = root["#"]
            for folder in path:
                if folder not in node:
                    node[folder] = {}
                node = node[folder]

        subtree_counts = defaultdict(int)
        subtree_serialization = {}

        def serialize(node):
            if not node:
                return "#"
            keys = []
            for folder in sorted(node.keys()):
                serialized_child = serialize(node[folder])
                keys.append(f"{folder}:{serialized_child}")
            key = "(" + ",".join(keys) + ")"
            subtree_counts[key] += 1
            subtree_serialization[id(node)] = key
            return key

        serialize(root["#"])

        to_delete = set()

        stack = [(root["#"], False)]

        while stack:
            node, visited = stack.pop()
            if visited:
                key = subtree_serialization.get(id(node), "")
                if key and subtree_counts[key] > 1 and key != "()":
                    to_delete.add(id(node))
                continue
            stack.append((node, True))
            for folder in reversed(list(node.keys())):
                stack.append((node[folder], False))

        result = []

        stack = [(root["#"], [])]

        while stack:
            node, current_path = stack.pop()
            for folder in node:
                if id(node[folder]) not in to_delete:
                    new_path = current_path + [folder]
                    result.append(new_path)
                    stack.append((node[folder], new_path))

        return result
