from math import comb
from collections import defaultdict

MOD = 10**9 + 7

class Solution:
    def waysToFillArray(self, queries: List[List[int]]) -> List[int]:
        max_k = max(q[1] for q in queries) if queries else 0
        spf = list(range(max_k + 1))
        for i in range(2, int(math.isqrt(max_k)) + 1):
            if spf[i] == i:
                for j in range(i*i, max_k + 1, i):
                    if spf[j] == j:
                        spf[j] = i

        def factorize(x):
            factors = defaultdict(int)
            if x == 1:
                return factors
            while x > 1:
                p = spf[x]
                while x % p == 0:
                    factors[p] += 1
                    x = x // p
            return factors

        factorizations = {}
        res = []
        comb_cache = {}

        for n, k in queries:
            if k == 1:
                res.append(1)
                continue
            if k not in factorizations:
                factorizations[k] = factorize(k)
            factors = factorizations[k]
            total = 1
            for cnt in factors.values():
                key = (n, cnt)
                if key not in comb_cache:
                    comb_cache[key] = comb(n + cnt - 1, cnt) % MOD
                total = total * comb_cache[key] % MOD
            res.append(total)
        return res
