        dp[0] = 1
        for num in nums:
            for j in range(k - 1, num - 1, -1):
                dp[j] = (dp[j] + dp[j - num]) % MOD

        total_partitions = pow(2, len(nums), MOD)
        invalid_partitions = 2 * sum(dp) % MOD
        return (total_partitions - invalid_partitions) % MOD
