class Solution:
    def collectTheCoins(self, coins: List[int], edges: List[List[int]]) -> int:
        n = len(coins)
        if n == 1:
            return 0

        graph = [[] for _ in range(n)]
        degree = [0] * n
        for a, b in edges:
            graph[a].append(b)
            graph[b].append(a)
            degree[a] += 1
            degree[b] += 1

        q = deque()
        for i in range(n):
            if degree[i] == 1 and coins[i] == 0:
                q.append(i)

        remaining_nodes = n
        while q:
            u = q.popleft()
            remaining_nodes -= 1
            for v in graph[u]:
                if degree[v] > 0:
                    degree[v] -= 1
                    if degree[v] == 1 and coins[v] == 0:
                        q.append(v)

        q = deque()
        for i in range(n):
            if degree[i] == 1 and coins[i] == 1:
                q.append(i)

        for _ in range(2):
            sz = len(q)
            if sz == 0:
                break
            for __ in range(sz):
                u = q.popleft()
                remaining_nodes -= 1
                for v in graph[u]:
                    if degree[v] > 0:
                        degree[v] -= 1
                        if degree[v] == 1:
                            q.append(v)

        if remaining_nodes <= 1:
            return 0
        return 2 * (remaining_nodes - 1)
