class Solution:
    def rootCount(self, edges: List[List[int]], guesses: List[List[int]], k: int) -> int:
        tree = defaultdict(list)
        for u, v in edges:
            tree[u].append(v)
            tree[v].append(u)

        guess_set = set((u, v) for u, v in guesses)

        parent = {}
        q = deque()
        q.append((0, None))
        correct = 0

        while q:
            node, par = q.popleft()
            parent[node] = par
            if par is not None and (par, node) in guess_set:
                correct += 1
            for neighbor in tree[node]:
                if neighbor != par:
                    q.append((neighbor, node))

        res = 0
        if correct >= k:
            res += 1

        q = deque()
        q.append((0, correct))
        while q:
            node, current_correct = q.popleft()
            for neighbor in tree[node]:
                if neighbor == parent[node]:
                    continue
                new_correct = current_correct
                if (node, neighbor) in guess_set:
                    new_correct -= 1
                if (neighbor, node) in guess_set:
                    new_correct += 1
                if new_correct >= k:
                    res += 1
                q.append((neighbor, new_correct))

        return res
