        n = len(nums1)
        dp = [float('inf')] * (1 << n)
        dp[0] = 0

        for mask in range(1 << n):
            cnt = bin(mask).count('1')
            if cnt >= n:
                continue
            for j in range(n):
                if not (mask & (1 << j)):
                    next_mask = mask | (1 << j)
                    xor = nums1[cnt] ^ nums2[j]
                    if dp[mask] + xor < dp[next_mask]:
                        dp[next_mask] = dp[mask] + xor

