class Solution:
    def numberOfComponents(self, properties: List[List[int]], k: int) -> int:
        n = len(properties)
        parent = [i for i in range(n)]

        def find(u):
            while parent[u] != u:
                parent[u] = parent[parent[u]]
                u = parent[u]
            return u

        def union(u, v):
            root_u = find(u)
            root_v = find(v)
            if root_u != root_v:
                parent[root_v] = root_u

        element_to_indices = {}
        prop_sets = []
        for idx, prop in enumerate(properties):
            prop_set = set(prop)
            prop_sets.append(prop_set)
            for num in prop_set:
                if num not in element_to_indices:
                    element_to_indices[num] = []
                element_to_indices[num].append(idx)

        for num in element_to_indices:
            indices = element_to_indices[num]
            if len(indices) >= 2:
                first = indices[0]
                for idx in indices[1:]:
                    union(first, idx)


        roots = [find(i) for i in range(n)]
        root_to_members = {}
        for i in range(n):
            root = roots[i]
            if root not in root_to_members:
                root_to_members[root] = []
            root_to_members[root].append(i)

        root_list = list(root_to_members.keys())
        for i in range(len(root_list)):
            for j in range(i + 1, len(root_list)):
                root1 = root_list[i]
                root2 = root_list[j]
                found = False
                for u in root_to_members[root1]:
                    if found:
                        break
                    for v in root_to_members[root2]:
                        if len(prop_sets[u] & prop_sets[v]) >= k:
                            union(u, v)
                            found = True
                            break

        roots = set()
        for i in range(n):
            roots.add(find(i))

        return len(roots)
