            s, e = intervals[0]
            duration = e - s
            return max(eventTime - duration, intervals[0][0], eventTime - intervals[0][1])

        prefix_max = [0] * (n + 2)
        suffix_max = [0] * (n + 2)

        for i in range(1, n + 1):
            prefix_max[i] = max(prefix_max[i-1], gaps[i-1])

        for i in range(n, -1, -1):
            suffix_max[i] = max(suffix_max[i+1], gaps[i])

        res = max_gap

        for i in range(n):
            s, e = intervals[i]
            duration = e - s

            if i > 0:
                new_start = intervals[i-1][1]
                new_end = new_start + duration
                if i < n - 1 and new_end > intervals[i+1][0]:
                    pass  # overlaps with next meeting
                else:
                    new_gap_before = new_start - (intervals[i-1][1] if i > 0 else 0)
                    new_gap_after = (intervals[i+1][0] - new_end if i < n - 1 else eventTime - new_end)
                    current_max = max(prefix_max[i], suffix_max[i+1])
                    current_max = max(current_max, new_gap_before, new_gap_after)
                    res = max(res, current_max)

            if i < n - 1:
                new_end = intervals[i+1][0]
                new_start = new_end - duration
                if i > 0 and new_start < intervals[i-1][1]:
                    pass  # overlaps with previous meeting
                else:
                    new_gap_before = new_start - (intervals[i-1][1] if i > 0 else 0)
                    new_gap_after = (intervals[i+1][0] - new_end if i < n - 1 else eventTime - new_end)
                    current_max = max(prefix_max[i], suffix_max[i+1])
                    current_max = max(current_max, new_gap_before, new_gap_after)
                    res = max(res, current_max)

            if i > 0:
                new_start = 0
                new_end = duration
                if new_end > intervals[0][0] if i == 0 else intervals[i-1][1]:
                    pass  # overlaps
                else:
                    new_gap_after = (intervals[0][0] - new_end if i == 0 else (intervals[i][0] - new_end if i < n else eventTime - new_end))
                    current_max = new_gap_after
                    if i > 0:
                        current_max = max(current_max, prefix_max[i])
                    if i < n - 1:
                        current_max = max(current_max, suffix_max[i+1])
                    res = max(res, current_max)

            if i < n - 1:
                new_start = eventTime - duration
                new_end = eventTime
                if new_start < intervals[-1][1] if i == n - 1 else intervals[i+1][0] - duration:
                    pass  # overlaps
                else:
                    new_gap_before = new_start - (intervals[-1][1] if i == n - 1 else intervals[i][1])
                    current_max = new_gap_before
                    if i > 0:
                        current_max = max(current_max, prefix_max[i])
                    if i < n - 1:
                        current_max = max(current_max, suffix_max[i+1])
                    res = max(res, current_max)

        return res
