from collections import defaultdict
from typing import List

class Solution:
    def minMaxWeight(self, n: int, edges: List[List[int]], threshold: int) -> int:
        if not edges:
            return 0 if n == 1 else -1

        max_weight = max(w for _, _, w in edges)
        left = 1
        right = max_weight
        answer = -1

        reversed_edges = defaultdict(list)
        for a, b, w in edges:
            reversed_edges[b].append((a, w))

        def is_possible(max_w):
            adj = defaultdict(list)
            for b in reversed_edges:
                for a, w in reversed_edges[b]:
                    if w <= max_w:
                        adj[b].append(a)

            out_degree = [0] * n
            heap = []
            visited = [False] * n
            visited[0] = True
            for neighbor in adj[0]:
                heapq.heappush(heap, (0, neighbor))
                out_degree[0] += 1

            while heap:
                parent, u = heapq.heappop(heap)
                if visited[u]:
                    continue
                visited[u] = True
                if out_degree[parent] >= threshold:
                    return False
                out_degree[parent] += 1
                for v in adj[u]:
                    if not visited[v]:
                        heapq.heappush(heap, (u, v))

            return all(visited)

        while left <= right:
            mid = (left + right) // 2
            if is_possible(mid):
                answer = mid
                right = mid - 1
            else:
                left = mid + 1
        return answer
