                valid = True
                k = j
                prev_diff = current_diff
                temp_dp = dp[j]
                while temp_dp > 1:
                    found = False
                    for l in range(k-1, -1, -1):
                        if dp[l] == temp_dp - 1 and abs(nums[k] - nums[l]) >= prev_diff:
