                    for k in range(len(rectangles)):
                        if max_y[k] <= y1:
                            lower += 1
                        elif min_y[k] >= y2:
                            upper += 1
                        else:
                            middle += 1
                        if lower > 0 and middle > 0 and upper > 0:
