import math
from collections import deque

class Solution:
    def minOperations(self, n: int, m: int) -> int:
        def is_prime(num):
            if num < 2:
                return True
            for i in range(2, int(math.sqrt(num)) + 1):
                if num % i == 0:
                    return False
            return True

        if is_prime(m):
            return -1

        visited = {}
        queue = deque()
        queue.append((n, 0))
        visited[n] = 0

        while queue:
            current, cost = queue.popleft()
            if current == m:
                return cost + m

            digits = list(map(int, str(current)))
            for i in range(len(digits)):
                original_digit = digits[i]
                if original_digit != 9:
                    new_digits = digits.copy()
                    new_digits[i] += 1
                    new_num = int(''.join(map(str, new_digits)))
                    new_cost = cost + current
                    if not is_prime(new_num) and (new_num not in visited or new_cost < visited[new_num]):
                        visited[new_num] = new_cost
                        queue.append((new_num, new_cost))
                if original_digit != 0:
                    new_digits = digits.copy()
                    new_digits[i] -= 1
                    new_num = int(''.join(map(str, new_digits)))
                    new_cost = cost + current
                    if not is_prime(new_num) and (new_num not in visited or new_cost < visited[new_num]):
                        visited[new_num] = new_cost
                        queue.append((new_num, new_cost))

        return -1
