from typing import List

class Solution:
    def maxRemoval(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)
        diff = [0] * (n + 2)

        for l, r in queries:
            diff[l] += 1
            diff[r + 1] -= 1

        coverage = [0] * n
        current = 0
        for i in range(n):
            current += diff[i]
            coverage[i] = current

        for i in range(n):
            if nums[i] > coverage[i]:
                return -1

        surplus = [coverage[i] - nums[i] for i in range(n)]

        class FenwickTree:
            def __init__(self, size):
                self.n = size
                self.tree = [float('inf')] * (self.n + 2)

            def update(self, index, value):
                while index <= self.n:
                    if value < self.tree[index]:
                        self.tree[index] = value
                    index += index & -index

            def query(self, index):
                res = float('inf')
                while index > 0:
                    if self.tree[index] < res:
                        res = self.tree[index]
                    index -= index & -index
                return res

        ft = FenwickTree(n)
        for i in range(n):
            ft.update(i + 1, surplus[i])

        removable = 0
        for l, r in queries:
            pass

        log_table = [0] * (n + 1)
        for i in range(2, n + 1):
            log_table[i] = log_table[i // 2] + 1

        k = log_table[n] + 1
        st = [[0] * n for _ in range(k)]
        st[0] = surplus.copy()

        for j in range(1, k):
            for i in range(n - (1 << j) + 1):
                st[j][i] = min(st[j - 1][i], st[j - 1][i + (1 << (j - 1))])

        def query_min(l, r):
            length = r - l + 1
            j = log_table[length]
            return min(st[j][l], st[j][r - (1 << j) + 1])

        removable = 0
        for l, r in queries:
            min_surplus = query_min(l, r)
            if min_surplus >= 1:
                removable += 1

        return removable
