class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        graph = [[] for _ in range(n)]
        for i in range(n - 1):
            graph[i].append((i + 1, 1))

        answer = []

        for i in range(len(queries)):
            u, v = queries[i]
            graph[u].append((v, 1))

            dist = [float('inf')] * n
            dist[0] = 0
            heap = [(0, 0)]

            while heap:
                current_dist, u_node = heapq.heappop(heap)
                if u_node == n - 1:
                    break
                if current_dist > dist[u_node]:
                    continue
                for v_node, weight in graph[u_node]:
                    if dist[v_node] > dist[u_node] + weight:
                        dist[v_node] = dist[u_node] + weight
                        heapq.heappush(heap, (dist[v_node], v_node))

            answer.append(dist[n - 1])

        return answer
