                for i2 in range(i1, rows):
                    x_total = prefix_x[i2+1][cols] - prefix_x[i1][cols] - prefix_x[i2+1][j1] + prefix_x[i1][j1]
                    y_total = prefix_y[i2+1][cols] - prefix_y[i1][cols] - prefix_y[i2+1][j1] + prefix_y[i1][j1]
                    if x_total < 1:
