class Solution:
    def minAnagramLength(self, s: str) -> int:
        n = len(s)
        freq = Counter(s)

        def find_gcd_of_counts():
            counts = list(freq.values())
            current_gcd = counts[0]
            for num in counts[1:]:
                current_gcd = gcd(current_gcd, num)
                if current_gcd == 1:
                    break
            return current_gcd

        total_gcd = find_gcd_of_counts()

        min_t = n
        for k in range(1, total_gcd + 1):
