class Solution:
    def maximumScoreAfterOperations(self, edges: List[List[int]], values: List[int]) -> int:
        n = len(values)
        tree = [[] for _ in range(n)]
        for a, b in edges:
            tree[a].append(b)
            tree[b].append(a)

        parent = [-1] * n
        stack = [(0, False)]
        order = []

        while stack:
            node, visited = stack.pop()
            if visited:
                order.append(node)
                continue
            stack.append((node, True))
            for neighbor in tree[node]:
                if neighbor != parent[node]:
                    parent[neighbor] = node
                    stack.append((neighbor, False))

        dp_take = [0] * n  # max score if we take this node
        dp_notake = [0] * n  # max score if we don't take this node

        for node in reversed(order):
            is_leaf = (node != 0 and len(tree[node]) == 1)
            if is_leaf:
                dp_take[node] = values[node]
                dp_notake[node] = 0
            else:
                sum_take = 0
                sum_notake = 0
                min_diff = float('inf')
                has_child = False
                for child in tree[node]:
                    if child != parent[node]:
                        has_child = True
                        sum_take += dp_take[child]
                        sum_notake += dp_notake[child]
                        min_diff = min(min_diff, dp_take[child] - dp_notake[child])
                dp_take[node] = values[node] + sum_take
                if has_child:
                    dp_notake[node] = sum_take - min_diff
                else:
                    dp_notake[node] = 0

        return max(dp_take[0], dp_notake[0])
