        for i in range(n):
            for j in range(m):
                if i == 0 and j == 0:
                    prefix[i][j] = grid[i][j] % MOD
                elif j == 0:
                    prefix[i][j] = (prefix[i-1][-1] * grid[i][j]) % MOD
                else:
