        for i in range(n-1, -1, -1):
            for j in range(m-1, -1, -1):
                if i == n-1 and j == m-1:
                    suffix[i][j] = grid[i][j] % MOD
                elif j == m-1:
                    suffix[i][j] = (suffix[i+1][0] * grid[i][j]) % MOD
                else:
                    suffix[i][j] = (suffix[i][j+1] * grid[i][j]) % MOD

        result = [[1 for _ in range(m)] for _ in range(n)]
        for i in range(n):
            for j in range(m):
                left = prefix[i-1][-1] if i > 0 else 1
                right = suffix[i+1][0] if i < n-1 else 1
                if j > 0:
                    left = prefix[i][j-1]
                if j < m-1:
                    right = suffix[i][j+1] if i == n-1 else (suffix[i][j+1] * suffix[i+1][0]) % MOD
                product = (left * right) % MOD
                result[i][j] = product

