class Solution:
    def mostProfitablePath(self, edges: List[List[int]], bob: int, amount: List[int]) -> int:
        n = len(edges) + 1
        graph = [[] for _ in range(n)]
        for a, b in edges:
            graph[a].append(b)
            graph[b].append(a)

        parent = [-1] * n
        q = deque([0])
        while q:
            u = q.popleft()
            for v in graph[u]:
                if parent[v] == -1 and v != 0:
                    parent[v] = u
                    q.append(v)

        bob_path = []
        u = bob
        while u != -1:
            bob_path.append(u)
            u = parent[u]
        bob_path.reverse()

        bob_time = {}
        for t, node in enumerate(bob_path):
            bob_time[node] = t

        max_profit = -float('inf')
        q = deque()
        q.append((0, 0, 0))  # (node, time, current_profit)
        visited = [False] * n
        visited[0] = True

        while q:
            node, time, profit = q.popleft()

            if node in bob_time:
                if time < bob_time[node]:
                    current_amount = amount[node]
                elif time == bob_time[node]:
                    current_amount = amount[node] // 2
                else:
                    current_amount = 0
            else:
                current_amount = amount[node]

            new_profit = profit + current_amount

            is_leaf = True
            for neighbor in graph[node]:
                if not visited[neighbor] and neighbor != parent[node]:
                    is_leaf = False
                    visited[neighbor] = True
                    q.append((neighbor, time + 1, new_profit))

            if is_leaf:
                max_profit = max(max_profit, new_profit)

        return max_profit
