from typing import List

class Solution:
    def longestSpecialPath(self, edges: List[List[int]], nums: List[int]) -> List[int]:
        tree = defaultdict(list)
        for u, v, length in edges:
            tree[u].append((v, length))
            tree[v].append((u, length))

        root = 0
        parent = {root: -1}
        children = defaultdict(list)
        stack = [root]
        while stack:
            node = stack.pop()
            for neighbor, length in tree[node]:
                if neighbor != parent[node]:
                    parent[neighbor] = node
                    children[node].append((neighbor, length))
                    stack.append(neighbor)

        max_length = 0
        min_nodes = float('inf')

        def dfs(node):
            nonlocal max_length, min_nodes
            current_val = nums[node]
            paths = []
            paths.append((0, 1, {current_val: 1}))

            for child, length in children[node]:
                child_length, child_nodes, child_freq = dfs(child)
                new_length = child_length + length
                new_nodes = child_nodes + 1
                new_freq = child_freq.copy()
                new_freq[current_val] = new_freq.get(current_val, 0) + 1
                paths.append((new_length, new_nodes, new_freq))

            for length, nodes, freq in paths:
                duplicates = 0
                for count in freq.values():
                    if count > 1:
                        duplicates += 1
                        if duplicates > 1:
                            break
                if duplicates <= 1:
                    if length > max_length:
                        max_length = length
                        min_nodes = nodes
                    elif length == max_length:
                        if nodes < min_nodes:
                            min_nodes = nodes

            best_length = 0
            best_nodes = 1
            best_freq = {current_val: 1}

            for length, nodes, freq in paths:
                duplicates = 0
                for count in freq.values():
                    if count > 1:
                        duplicates += 1
                        if duplicates > 1:
                            break
                if duplicates <= 1:
                    if length > best_length:
                        best_length = length
                        best_nodes = nodes
                        best_freq = freq.copy()
                    elif length == best_length and nodes < best_nodes:
                        best_nodes = nodes
                        best_freq = freq.copy()

            return (best_length, best_nodes, best_freq)

        dfs(root)
        return [max_length, min_nodes]
