    def maxSubarrays(self, n: int, conflictingPairs: List[List[int]]) -> int:
        if not conflictingPairs:
            return n * (n + 1) // 2

        max_subarrays = 0

        for i in range(len(conflictingPairs)):
            temp_pairs = conflictingPairs[:i] + conflictingPairs[i+1:]
            conflict_dict = {}
            for a, b in temp_pairs:
                if a > b:
                    a, b = b, a
                if a not in conflict_dict:
                    conflict_dict[a] = set()
                conflict_dict[a].add(b)

            total = n * (n + 1) // 2
            invalid = 0
            left = 0
            right = 1
            last_conflict_pos = {}

            for right in range(1, n + 1):
                current = right
                max_left = left
                if current in last_conflict_pos:
                    max_left = max(max_left, last_conflict_pos[current])

                for a in conflict_dict:
                    if a <= current and a >= left + 1:
                        for b in conflict_dict[a]:
                            if b <= current:
                                max_left = max(max_left, a)
                left = max_left
                invalid += right - left

            current_subarrays = total - invalid
            if current_subarrays > max_subarrays:
                max_subarrays = current_subarrays

        return max_subarrays
