                    j += 1
        else:
            middle_matches = []
            for i in range(n - m_middle + 1):
                if s[i:i+m_middle] == middle:
                    middle_matches.append(i)

            i = 0
            j = 0
            k = 0
            len_prefix = len(prefix_matches)
            len_middle = len(middle_matches)
            len_suffix = len(suffix_matches)
            while i < len_prefix and j < len_middle and k < len_suffix:
                prefix_end = prefix_matches[i]
                middle_start = middle_matches[j]
                suffix_start = suffix_matches[k]

                if middle_start >= prefix_end:
                    if suffix_start >= middle_start + m_middle:
                        min_len = min(min_len, suffix_start - prefix_end)
                        i += 1
                    else:
                        k += 1
                else:
                    j += 1

        return min_len if min_len != float('inf') else -1
