class Solution:
    def minLength(self, s: str, numOps: int) -> int:
        n = len(s)
        if n == 0:
            return 0

        def get_min_segment(target_char):
            segments = []
            current = 0
            for c in s:
                if c == target_char:
                    current += 1
                else:
                    if current > 0:
                        segments.append(current)
                    current = 0
            if current > 0:
                segments.append(current)
            if not segments:
                return 0
            segments.sort()
            ops = numOps
            i = len(segments) - 1
            while ops > 0 and i >= 0:
                max_seg = segments[i]
                if i == 0:
                    reduce = min(ops, max_seg)
                    segments[i] -= reduce
                    ops -= reduce
                else:
                    next_seg = segments[i-1]
                    diff = max_seg - next_seg
                    reduce = min(ops, diff)
                    segments[i] -= reduce
                    ops -= reduce
                i -= 1
            segments.sort()
            return segments[-1] if segments else 0

        res0 = get_min_segment('0')
        res1 = get_min_segment('1')

        if numOps == 0:
            return max(res0, res1)

        return min(res0, res1)
