class Solution:
    def maximizeSumOfWeights(self, edges: List[List[int]], k: int) -> int:
        adjacency = defaultdict(list)
        edge_info = {}
        for u, v, w in edges:
            adjacency[u].append((v, w))
            adjacency[v].append((u, w))
            edge_info[(u, v)] = w
            edge_info[(v, u)] = w

        degrees = defaultdict(int)
        for u, v, _ in edges:
            degrees[u] += 1
            degrees[v] += 1

        heap = []
        for u in degrees:
            if degrees[u] > k:
                heapq.heappush(heap, (-degrees[u], u))

        edge_removed = set()
        total = sum(w for _, _, w in edges)
        removed_sum = 0

        while heap:
            neg_degree, u = heapq.heappop(heap)
            current_degree = -neg_degree
            if current_degree <= k:
                continue

            min_w = float('inf')
            min_edge = None
            for v, w in adjacency[u]:
                if (u, v) not in edge_removed and (v, u) not in edge_removed:
                    if w < min_w:
                        min_w = w
                        min_edge = (u, v)

            if min_edge is not None:
                edge_removed.add(min_edge)
                removed_sum += min_w
                a, b = min_edge
                degrees[a] -= 1
                degrees[b] -= 1
                if degrees[a] > k:
                    heapq.heappush(heap, (-degrees[a], a))
                if degrees[b] > k:
                    heapq.heappush(heap, (-degrees[b], b))

        return total - removed_sum
