class Solution:
    def possibleStringCount(self, word: str, k: int) -> int:
        MOD = 10**9 + 7
        n = len(word)
        if k > n:
            return 0

        groups = []
        current_char = word[0]
        count = 1
        for c in word[1:]:
            if c == current_char:
                count += 1
            else:
                groups.append(count)
                current_char = c
                count = 1
        groups.append(count)

        m = len(groups)
        if m < k:
            return 0

        dp = [[0] * (m + 1) for _ in range(m + 1)]
        dp[0][0] = 1

        for i in range(1, m + 1):
            for j in range(1, m + 1):
                if j > i:
                    continue
                dp[i][j] = dp[i-1][j-1]
                if i >= 2 and j >= 1:
                    dp[i][j] += dp[i-1][j] * (groups[i-1] - 1)
                dp[i][j] %= MOD

        total = 0
        for l in range(k, m + 1):
            total = (total + dp[m][l]) % MOD
        return total
