class Solution:
    def constructGridLayout(self, n: int, edges: List[List[int]]) -> List[List[int]]:
        graph = defaultdict(set)
        for u, v in edges:
            graph[u].add(v)
            graph[v].add(u)

        start = 0
        for node in graph:
            if len(graph[node]) <= 2:
                start = node
                break

        visited = {}
        queue = deque()
        queue.append((start, 0, 0))
        visited[start] = (0, 0)
        min_row = max_row = min_col = max_col = 0

        while queue:
            node, row, col = queue.popleft()
            for neighbor in graph[node]:
                if neighbor not in visited:
                    if (row - 1, col) not in visited.values():
                        new_row, new_col = row - 1, col
                    elif (row + 1, col) not in visited.values():
                        new_row, new_col = row + 1, col
                    elif (row, col - 1) not in visited.values():
                        new_row, new_col = row, col - 1
                    else:
                        new_row, new_col = row, col + 1

                    visited[neighbor] = (new_row, new_col)
                    queue.append((neighbor, new_row, new_col))

                    min_row = min(min_row, new_row)
                    max_row = max(max_row, new_row)
                    min_col = min(min_col, new_col)
                    max_col = max(max_col, new_col)

        rows = max_row - min_row + 1
        cols = max_col - min_col + 1
        grid = [[-1 for _ in range(cols)] for _ in range(rows)]

        for node, (row, col) in visited.items():
            adjusted_row = row - min_row
            adjusted_col = col - min_col
            grid[adjusted_row][adjusted_col] = node

        return grid
