    def maxMoves(self, kx: int, ky: int, positions: List[List[int]]) -> int:
        pawns = positions
        n = len(pawns)
        distance_maps = {}
        directions = [(-2, -1), (-2, 1), (-1, -2), (-1, 2),
                      (1, -2), (1, 2), (2, -1), (2, 1)]

        for i, (px, py) in enumerate(pawns):
            q = deque()
            q.append((px, py))
            dist = {}
            dist[(px, py)] = 0
            while q:
                x, y = q.popleft()
                for dx, dy in directions:
                    nx, ny = x + dx, y + dy
                    if 0 <= nx < 50 and 0 <= ny < 50 and (nx, ny) not in dist:
                        dist[(nx, ny)] = dist[(x, y)] + 1
                        q.append((nx, ny))
            distance_maps[i] = dist

        initial_steps = []
        for i in range(n):
            if (kx, ky) in distance_maps[i]:
                initial_steps.append(distance_maps[i][(kx, ky)])
            else:
                initial_steps.append(0)

        step_matrix = [[0] * n for _ in range(n)]
        for i in range(n):
            for j in range(n):
                if i != j:
                    px, py = pawns[j]
                    if (px, py) in distance_maps[i]:
                        step_matrix[i][j] = distance_maps[i][(px, py)]
                    else:
                        step_matrix[i][j] = 0

        steps = initial_steps.copy()
        used = [False] * n
        total = 0
        turn = 0  # 0 for Alice, 1 for Bob

        while True:
            max_step = -1
            max_idx = -1
            for i in range(n):
                if not used[i] and steps[i] > max_step:
                    max_step = steps[i]
                    max_idx = i
            if max_step == -1:
                break
            used[max_idx] = True
            if turn == 0:
                total += max_step
            turn ^= 1
            for j in range(n):
                if not used[j] and step_matrix[max_idx][j] < steps[j]:
                    steps[j] = step_matrix[max_idx][j]

        return total
