class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], queries: List[List[int]]) -> List[int]:
        n = len(colors)
        if n == 0:
            return []

        def build_segments():
            segments = []
            start = 0
            for i in range(1, n + 1):
                if i == n:
                    if colors[i-1] != colors[0]:
                        continue
                    else:
                        segments.append((start, i-1))
                        start = i
                elif colors[i] == colors[i-1]:
                    segments.append((start, i-1))
                    start = i
            if start < n:
                segments.append((start, n-1))

            if len(segments) > 1 and colors[segments[0][0]] == colors[segments[-1][1]]:
                first_start, first_end = segments[0]
                last_start, last_end = segments[-1]
                merged_segment = (last_start, first_end)
                segments = [merged_segment] + segments[1:-1]
            return segments

        segments = build_segments()
        count_dict = defaultdict(int)
        for start, end in segments:
            length = end - start + 1
            count_dict[length] += 1

        result = []
        for query in queries:
            if query[0] == 1:
                size = query[1]
                res = 0
                for seg_len in count_dict:
                    if seg_len >= size:
                        res += (seg_len - size + 1) * count_dict[seg_len]
                result.append(res)
            elif query[0] == 2:
                index, color = query[1], query[2]
                if colors[index] == color:
                    continue
                colors[index] = color
                segments = build_segments()
                count_dict = defaultdict(int)
                for start, end in segments:
                    length = end - start + 1
                    count_dict[length] += 1
        return result
