class Solution:
    def numberOfPermutations(self, n: int, requirements: List[List[int]]) -> int:
        MOD = 10**9 + 7
        req = {endi: cnti for endi, cnti in requirements}
        max_inv = max(req.values()) if req else 0

        dp = [[0] * (max_inv + 1) for _ in range(n)]
        dp[0][0] = 1

        for i in range(1, n):
            max_possible_inv = i * (i + 1) // 2
            for j in range(0, min(max_possible_inv, max_inv) + 1):
                for k in range(0, min(i + 1, j + 1)):
                    if j - k >= 0:
                        dp[i][j] = (dp[i][j] + dp[i-1][j - k]) % MOD

        result = 1
        for endi in sorted(req.keys()):
            cnti = req[endi]
            if cnti > endi * (endi + 1) // 2:
                return 0
            if endi == 0:
                if cnti != 0:
                    return 0
                else:
                    continue
            result = (result * dp[endi][cnti]) % MOD

        return result
