class Solution:
    def minCostToEqualizeArray(self, nums: List[int], cost1: int, cost2: int) -> int:
        MOD = 10**9 + 7
        max_num = max(nums)
        min_num = min(nums)
        total_deficit = sum(max_num - x for x in nums)

        if cost1 * 2 <= cost2:
            return (total_deficit * cost1) % MOD

        n = len(nums)
        min_cost = float('inf')

        for target in range(max_num, max_num + 2):
            deficits = [target - x for x in nums]
            total = sum(deficits)
            max_deficit = max(deficits)
            pair_ops = (total - max_deficit)
            if max_deficit <= pair_ops:
                cost = (total // 2) * cost2
                if total % 2 != 0:
                    cost += cost1
            else:
                cost = pair_ops * cost2 + (max_deficit - pair_ops) * cost1
            if cost < min_cost:
                min_cost = cost

        return min_cost % MOD
