class Solution:
    def earliestSecondToMarkIndices(self, nums: List[int], changeIndices: List[int]) -> int:
        n = len(nums)
        m = len(changeIndices)

        left = 1
        right = m
        answer = -1

        while left <= right:
            mid = (left + right) // 2
            last_occurrence = {}
            for s in range(mid):
                i = changeIndices[s] - 1
                last_occurrence[i] = s

            if len(last_occurrence) != n:
                left = mid + 1
                continue

            total_ops = 0
            possible = True
            for s in range(mid):
                i = changeIndices[s] - 1
                if last_occurrence[i] == s:
                    required = nums[i]
                    if required > total_ops:
                        possible = False
                        break
                    total_ops -= required
                    total_ops += 1  # marking takes 1 operation
                else:
                    total_ops += 1  # decrement or set operation

            if possible:
                answer = mid
                right = mid - 1
            else:
                left = mid + 1

        return answer
