        s_len = len(s)
        s_num = int(s)

        def count_powerful_numbers(high):
            high_str = str(high)
            n = len(high_str)
            if n < s_len:
                return 0
            prefix_len = n - s_len
            prefix_high = int(high_str[:prefix_len]) if prefix_len > 0 else 0
            suffix = int(high_str[prefix_len:])

            dp = {}
            def dfs(pos, tight):
                if pos == prefix_len:
                    return 1
                key = (pos, tight)
                if key in dp:
                    return dp[key]
                limit_digit = int(high_str[pos]) if tight else limit
                limit_digit = min(limit_digit, limit)
                res = 0
                for d in range(0, limit_digit + 1):
                    new_tight = tight and (d == int(high_str[pos]))
                    res += dfs(pos + 1, new_tight)
                dp[key] = res
                return res
            total_prefix = dfs(0, True)

            if suffix >= s_num and all(int(c) <= limit for c in high_str[prefix_len:]):
                return total_prefix
            else:
                return total_prefix - 1 if total_prefix > 0 else 0

        def count_powerful_numbers_simple(high):
            count = 0
            s_num = int(s)
            if s_num > high:
                return 0
            max_prefix = high // (10 ** s_len)
            for prefix in range(0, max_prefix + 1):
                num = prefix * (10 ** s_len) + s_num
                if num > high:
                    continue
                valid = True
                temp = prefix
                while temp > 0:
                    digit = temp % 10
                    if digit > limit:
                        valid = False
                        break
                    temp = temp // 10
                if valid:
                    count += 1
            return count

        if finish < 10**6:
            return count_powerful_numbers_simple(finish) - count_powerful_numbers_simple(start - 1)
        else:
            return count_powerful_numbers(finish) - count_powerful_numbers(start - 1)
