class Solution:
    def leftmostBuildingQueries(self, heights: List[int], queries: List[List[int]]) -> List[int]:
        n = len(heights)
        res = [-1] * len(queries)
        processed_queries = []

        for idx, (a, b) in enumerate(queries):
            if a == b:
                res[idx] = a
                continue
            if a > b:
                a, b = b, a
            if heights[a] < heights[b]:
                res[idx] = b
                continue
            processed_queries.append((b, heights[a], idx))

        stack = []
        queries_sorted = sorted(processed_queries, key=lambda x: -x[0])
        ptr = len(queries_sorted) - 1

        for i in range(n - 1, -1, -1):
            h = heights[i]
            while ptr >= 0 and queries_sorted[ptr][0] == i:
                _, min_h, idx = queries_sorted[ptr]
                ptr -= 1
                left = bisect.bisect_right(stack, (-min_h, float('inf'))) 
                if left < len(stack):
                    res[idx] = stack[left][1]
                else:
                    res[idx] = -1

            while stack and stack[-1][0] >= -h:
                stack.pop()
            stack.append((-h, i))

        return res
