class Solution:
    def maximumPoints(self, edges: List[List[int]], coins: List[int], k: int) -> int:
        n = len(coins)
        graph = [[] for _ in range(n)]
        for a, b in edges:
            graph[a].append(b)
            graph[b].append(a)

        parent = [-1] * n
        children = [[] for _ in range(n)]
        q = deque([0])
        parent[0] = -2

        while q:
            u = q.popleft()
            for v in graph[u]:
                if parent[v] == -1:
                    parent[v] = u
                    children[u].append(v)
                    q.append(v)

        max_halves = 14
        dp = [[0] * (max_halves + 1) for _ in range(n)]

        stack = [(0, False)]

        while stack:
            u, processed = stack.pop()
            if not processed:
                stack.append((u, True))
                for v in reversed(children[u]):
                    stack.append((v, False))
            else:
                for h in range(max_halves, -1, -1):
                    current_coin = coins[u] >> h
                    option1 = max(current_coin - k, 0)
                    for v in children[u]:
                        option1 += dp[v][h]

                    option2 = current_coin // 2
                    for v in children[u]:
                        next_h = min(h + 1, max_halves)
                        option2 += dp[v][next_h]

                    dp[u][h] = max(option1, option2)

