from typing import List

class Solution:
    def countPaths(self, n: int, edges: List[List[int]]) -> int:
        is_prime = [True] * (n + 1)
        is_prime[0] = is_prime[1] = False
        for i in range(2, int(math.sqrt(n)) + 1):
            if is_prime[i]:
                for j in range(i * i, n + 1, i):
                    is_prime[j] = False

        adj = defaultdict(list)
        for u, v in edges:
            adj[u].append(v)
            adj[v].append(u)

        res = 0

        def dfs(node, parent):
            nonlocal res
            cnt = [0, 0]  # cnt[0]: non-prime count, cnt[1]: prime count
            if is_prime[node]:
                cnt[1] = 1
            else:
                cnt[0] = 1

            for neighbor in adj[node]:
                if neighbor == parent:
                    continue
                child_cnt = dfs(neighbor, node)
                res += child_cnt[0] * cnt[1] + child_cnt[1] * cnt[0]
                if is_prime[node]:
                    cnt[1] += child_cnt[0]
                else:
                    cnt[0] += child_cnt[0]
                    cnt[1] += child_cnt[1]
            return cnt

        dfs(1, None)
        return res
