class Solution:
    def countSteppingNumbers(self, low: str, high: str) -> int:
        MOD = 10**9 + 7

        def count_up_to(s):
            n = len(s)
            num = list(map(int, s))

            from functools import lru_cache

            @lru_cache(maxsize=None)
            def dp(pos, prev, tight, leading_zero):
                if pos == n:
                    return 0 if leading_zero else 1

                limit = num[pos] if tight else 9
                total = 0

                for d in range(0, limit + 1):
                    new_tight = tight and (d == limit)
                    new_leading_zero = leading_zero and (d == 0)

                    if new_leading_zero:
                        total += dp(pos + 1, prev, new_tight, new_leading_zero)
                    else:
                        if leading_zero or abs(d - prev) == 1:
                            total += dp(pos + 1, d, new_tight, False)

                return total % MOD

            return dp(0, 0, True, True)

        def is_stepping(s):
            for i in range(1, len(s)):
                if abs(int(s[i]) - int(s[i-1])) != 1:
                    return False
            return True

        count_high = count_up_to(high)
        count_low = count_up_to(str(int(low) - 1)) if int(low) > 1 else 0
        res = (count_high - count_low) % MOD

        if is_stepping(low):
            res = (res + 1) % MOD

        return res
