class Solution:
    def minimumScore(self, s: str, t: str) -> int:
        m, n = len(s), len(t)
        left_pos = [-1] * n
        i = 0
        for j in range(n):
            while i < m and s[i] != t[j]:
                i += 1
            if i < m:
                left_pos[j] = i
                i += 1
            else:
                break

        right_pos = [-1] * n
        i = m - 1
        for j in range(n - 1, -1, -1):
            while i >= 0 and s[i] != t[j]:
                i -= 1
            if i >= 0:
                right_pos[j] = i
                i -= 1
            else:
                break

        left = 0
        while left < n and left_pos[left] != -1:
            left += 1

        right = n - 1
        while right >= 0 and right_pos[right] != -1:
            right -= 1

        if left > right:
            return 0

        min_score = right - left + 1

        i = 0
        j = right + 1
        while i < left and j < n:
            if left_pos[i] < right_pos[j]:
                min_score = min(min_score, j - i - 1)
                i += 1
            else:
                j += 1

        return min(min_score, right - left + 1)
