class Solution:
    def maxPower(self, stations: List[int], r: int, k: int) -> int:
        n = len(stations)
        left = min(stations) if n > 0 else 0
        right = max(stations) + k if n > 0 else k
        res = 0

        prefix = [0] * (n + 1)
        for i in range(n):
            prefix[i+1] = prefix[i] + stations[i]

        def get_power(i):
            start = max(0, i - r)
            end = min(n - 1, i + r)
            return prefix[end + 1] - prefix[start]

        initial_powers = [get_power(i) for i in range(n)]
        left = min(initial_powers)
        right = max(initial_powers) + k

        def is_possible(target):
            nonlocal k
            diff = [0] * (n + 1)
            current = 0
            remaining = k
            for i in range(n):
                current += diff[i]
                total = initial_powers[i] + current
                if total < target:
                    needed = target - total
                    if needed > remaining:
                        return False
                    remaining -= needed
                    current += needed
                    end = min(i + 2 * r + 1, n)
                    if end < n:
                        diff[end] -= needed
            return True

        while left <= right:
            mid = (left + right) // 2
            if is_possible(mid):
                res = mid
                left = mid + 1
            else:
                right = mid - 1
        return res
