class Solution:
    def treeQueries(self, root: Optional[TreeNode], queries: List[int]) -> List[int]:
        from collections import defaultdict

        depth = defaultdict(int)
        height = defaultdict(int)
        node_map = {}
        parent = {}
        children = defaultdict(list)

        stack = [(root, 0)]
        while stack:
            node, d = stack.pop()
            if not node:
                continue
            depth[node.val] = d
            node_map[node.val] = node
            if node.left:
                parent[node.left.val] = node.val
                children[node.val].append(node.left.val)
                stack.append((node.left, d + 1))
            if node.right:
                parent[node.right.val] = node.val
                children[node.val].append(node.right.val)
                stack.append((node.right, d + 1))

        stack = [(root, False)]
        while stack:
            node, visited = stack.pop()
            if not node:
                continue
            if visited:
                left_height = height[node.left.val] if node.left else -1
                right_height = height[node.right.val] if node.right else -1
                height[node.val] = 1 + max(left_height, right_height)
            else:
                stack.append((node, True))
                stack.append((node.right, False))
                stack.append((node.left, False))

        max_heights = defaultdict(dict)
        for node_val in node_map:
            current = node_val
            path = []
            while current in parent:
                current = parent[current]
                path.append(current)
            prev_max = -1
            for ancestor_val in reversed(path):
                sibling_height = -1
                for child in children[ancestor_val]:
                    if child != node_val and (ancestor_val not in parent or child != parent[node_val]):
                        sibling_height = max(sibling_height, height[child])
                current_max = depth[ancestor_val] + sibling_height
                if prev_max != -1:
                    current_max = max(current_max, prev_max)
                max_heights[node_val][ancestor_val] = current_max
                prev_max = current_max

        res = []
        for q in queries:
            if q not in parent:
                res.append(-1)
                continue
            path = []
            current = q
            while current in parent:
                current = parent[current]
                path.append(current)
            max_h = -1
            for ancestor_val in path:
                if ancestor_val in max_heights[q]:
                    max_h = max(max_h, max_heights[q][ancestor_val])
            res.append(max_h if max_h != -1 else -1)
        return res
