class Solution:
    def maximumBeauty(self, flowers: List[int], newFlowers: int, target: int, full: int, partial: int) -> int:
        flowers.sort()
        n = len(flowers)
        prefix = [0] * (n + 1)
        for i in range(n):
            prefix[i+1] = prefix[i] + flowers[i]

        res = 0
        j = n - 1
        while j >= 0 and flowers[j] >= target:
            j -= 1

        if j < 0:
            return n * full

        def compute_max_min(flowers, prefix, max_pos, available):
            low = flowers[0]
            high = target - 1
            best_min = 0
            while low <= high:
                mid = (low + high) // 2
                left, right = 0, max_pos
                pos = -1
                while left <= right:
                    m = (left + right) // 2
                    if flowers[m] < mid:
                        pos = m
                        left = m + 1
                    else:
                        right = m - 1
                if pos == -1:
                    high = mid - 1
                else:
                    total = mid * (pos + 1) - prefix[pos + 1]
                    if total <= available:
                        best_min = mid
                        low = mid + 1
                    else:
                        high = mid - 1
            return best_min

        for k in range(0, n - j + 1):
            if k == 0:
                best_min = compute_max_min(flowers, prefix, n - 1, newFlowers)
                current = best_min * partial
                if current > res:
                    res = current
            else:
                required = max(0, target * k - (prefix[n] - prefix[n - k]))
                if required > newFlowers:
                    continue
                remaining = newFlowers - required
                if n - k == 0:
                    current = n * full
                    if current > res:
                        res = current
                    continue
                best_min = compute_max_min(flowers, prefix, n - k - 1, remaining)
                current = k * full + best_min * partial
                if current > res:
                    res = current
        return res
