class Solution:
    def goodTriplets(self, nums1: List[int], nums2: List[int]) -> int:
        n = len(nums1)
        pos2 = [0] * n
        for idx, num in enumerate(nums2):
            pos2[num] = idx

        pos_in_nums2 = [0] * n
        for i in range(n):
            pos_in_nums2[i] = pos2[nums1[i]]

        class FenwickTree:
            def __init__(self, size):
                self.size = size
                self.tree = [0] * (self.size + 1)

            def update(self, index, delta=1):
                while index <= self.size:
                    self.tree[index] += delta
                    index += index & -index

            def query(self, index):
                res = 0
                while index > 0:
                    res += self.tree[index]
                    index -= index & -index
                return res

        ft = FenwickTree(n)
        res = 0
        for i in range(n):
            y = pos_in_nums2[i] + 1  # 1-based
            left = ft.query(y - 1)
            right = (n - 1 - pos_in_nums2[i]) - (i - left)
            res += left * right
            ft.update(y)
        return res
