            for t in range(1, max_steps + 1):
                current_positions = set()
                for i in range(n):
                    r, c = positions[i]
                    tr, tc = dests[i]
                    if (r, c) == (tr, tc):
                        pos = (r, c)
                    else:
                        dr = 1 if tr > r else (-1 if tr < r else 0)
                        dc = 1 if tc > c else (-1 if tc < c else 0)
                        step = min(t, steps[i])
                        pos = (r + dr * step, c + dc * step)
                    if pos in current_positions:
                        valid = False
                        break
                    current_positions.add(pos)
                if not valid:
                    break
            if valid:
                count += 1

        return count
