from typing import List
from collections import defaultdict

class Solution:
    def waysToPartition(self, nums: List[int], k: int) -> int:
        n = len(nums)
        prefix = [0] * (n + 1)
        for i in range(n):
            prefix[i+1] = prefix[i] + nums[i]
        total = prefix[n]

        res = 0
        for pivot in range(1, n):
            if prefix[pivot] == total - prefix[pivot]:
                res += 1

        left_counts = defaultdict(int)
        right_counts = defaultdict(int)

        for pivot in range(1, n):
            s = prefix[pivot]
            right_counts[2 * s - total] += 1

        max_changes = 0
        for i in range(n):
            delta = k - nums[i]
            cnt = right_counts.get(delta, 0) + left_counts.get(-delta, 0)

            if cnt > max_changes:
                max_changes = cnt

            if i < n - 1:
                pivot = i + 1
                s = prefix[pivot]
                key = 2 * s - total
                right_counts[key] -= 1
                left_counts[key] += 1

        return max(res, max_changes)
